import React, { useState } from "react";
import 'bootstrap/dist/css/bootstrap.css';

const Accordians=({ques,ans})=>{

    const [show,setShow]=useState(false);

   return(
    <>
      
       <div className="bg-light py-2 mx-5  my-4 rounded d-flex flex-row align-items-center">
        <button className="border-0 bg-light align-self-start pt-2" onClick={()=>{setShow(!show)}}>{
            !show?"+":"-"
        }
        </button>
        <p className="pt-2 fw-bold">{ques}</p>

        </div>
        {
            show &&  <p className="mx-5 bg-light">{ans}</p>
        }
       
        
    </>
    );
}

export default Accordians;
